function typeEffect(element,speed){let text=element.textContent;element.textContent="";let i=0;let timer=setInterval(function(){if(i<text.length){element.append(text.charAt(i));i++}else{clearInterval(timer)}},speed)}
const speed=175;const span1=document.getElementById('span1');const span2=document.getElementById('span2');setTimeout(()=>{span1.style.display="initial"
typeEffect(span1,speed)
setTimeout(()=>{span2.style.display="initial"
typeEffect(span2,speed)},5000)},4000);const canvas=document.querySelector('#canvas1');const ctx=canvas.getContext('2d');const sectionBandeau=document.getElementById('bandeau');const particulesArray=[];canvas.width=sectionBandeau.clientWidth;canvas.height=sectionBandeau.clientHeight;window.addEventListener('resize',(event)=>{canvas.width=sectionBandeau.clientWidth;canvas.height=sectionBandeau.clientHeight})
class Particule{constructor(){this.x=Math.random()*canvas.width;this.y=Math.random()*canvas.height;this.size=Math.random()*2+1;this.speedX=Math.random()*3-1.5;this.speedY=Math.random()*3-1.5}
update(){this.x+=this.speedX
this.y+=this.speedY}
draw(){ctx.fillStyle='white';ctx.beginPath();ctx.arc(this.x,this.y-75,this.size,0,Math.PI*2);ctx.fill()}}
function init(){for(let i=0;i<150;i++){particulesArray.push(new Particule())}}
init();function handlerParticles(){for(let i=0;i<particulesArray.length;i++){particulesArray[i].update();particulesArray[i].draw()}}
function animate(){ctx.clearRect(0,0,canvas.width,canvas.height);handlerParticles();requestAnimationFrame(animate)}
