const canvas=document.querySelector('#canvas1');const ctx=canvas.getContext('2d');const sectionBandeau=document.getElementById('bandeau');const particulesArray=[];canvas.width=sectionBandeau.clientWidth;canvas.height=sectionBandeau.clientHeight;window.addEventListener('resize',(event)=>{canvas.width=sectionBandeau.clientWidth;canvas.height=sectionBandeau.clientHeight})
class Particule{constructor(){this.x=Math.random()*canvas.width;this.y=Math.random()*canvas.height;this.size=Math.random()*3+1;this.speedX=Math.random()*3-1.5;this.speedY=Math.random()*3-1.5}
update(){this.x+=this.speedX
this.y+=this.speedY}
draw(){ctx.fillStyle='white';ctx.beginPath();ctx.arc(this.x,this.y-75,this.size,0,Math.PI*2);ctx.fill()}}
function init(){for(let i=0;i<150;i++){particulesArray.push(new Particule())}}
init();function handlerParticles(){for(let i=0;i<particulesArray.length;i++){particulesArray[i].update();particulesArray[i].draw()}}
function animate(){ctx.clearRect(0,0,canvas.width,canvas.height);handlerParticles();requestAnimationFrame(animate)}
animate()
const form=document.getElementById('contact_form');const firstName=document.getElementById('name_input');const email=document.getElementById('email_input');const numero=document.getElementById('telephone_input');const subject=document.getElementById('subject_input');const message=document.getElementById('message_input');firstName.addEventListener('blur',(event)=>{let error=document.getElementById('error1');error.innerText=''
if(event.target.value==""){error.style.color='red';error.innerText='Le nom est nécessaire'}})
email.addEventListener('input',(event)=>{let emailVerif=/^([0-9a-zA-Z].*?@([0-9a-zA-Z].*\.\w{2,4}))$/
let error=document.getElementById('error2');error.innerText=''
if(!emailVerif.test(event.target.value)){error.style.color='red';error.innerText='Le format de l\'email est incorrect'}})
numero.addEventListener('blur',(event)=>{let error=document.getElementById('error3');error.innerText=''
let numValide=/^0[1-9]\d{8}$/;if(!numValide.test(event.target.value)||event.target.value==""){error.style.color='red';error.innerText='Veuillez entrez votre numero'}})
form.addEventListener('submit',(event)=>{event.preventDefault();let formData={firstName:firstName.value,email:email.value,numero:numero.value,subject:subject.value,message:message.value}
let xhr=new XMLHttpRequest();xhr.open('POST','/');xhr.setRequestHeader('content-type','application/json');xhr.addEventListener('load',()=>{console.log(xhr.responseText);let info=document.getElementById('p-info');info.focus();if(xhr.responseText=='success'){info.style.color='green';info.style.fontStyle="italic";info.textContent='Message envoyé';firstName.value='';email.value='';numero.value='';subject.value='';message.value=''}else{info.style.color='red';info.style.fontStyle="italic";info.textContent='Un problème est survenue. Veuillez réessayer'}});xhr.send(JSON.stringify(formData))})